package com.jlt.vote.bis.wx.vo;import javax.validation.constraints.NotNull;import java.io.Serializable;/** * 预支付请求参数 * @Author gaoyan * @Date: 2017/7/8 */public class VotePrepayRequest implements Serializable {	/**	 * 用户id	 */	@NotNull(message="{vote.userId.invalid}")	private Long userId;	/**	 * 礼物id	 */	@NotNull(message="{vote.giftId.invalid}")	private Long giftId;	/**	 * 礼物数量     */	private Integer giftCount;	/**	 * 礼物留言	 */	private String remark;	public Long getUserId() {		return userId;	}	public void setUserId(Long userId) {		this.userId = userId;	}	public Long getGiftId() {		return giftId;	}	public void setGiftId(Long giftId) {		this.giftId = giftId;	}	public Integer getGiftCount() {		return giftCount;	}	public void setGiftCount(Integer giftCount) {		this.giftCount = giftCount;	}	public String getRemark() {		return remark;	}	public void setRemark(String remark) {		this.remark = remark;	}	@Override	public String toString() {		final StringBuilder sb = new StringBuilder("VotePrepayRequest{");		sb.append("userId=").append(userId);		sb.append(", giftId=").append(giftId);		sb.append(", giftCount=").append(giftCount);		sb.append(", remark='").append(remark).append('\'');		sb.append('}');		return sb.toString();	}}