mapper.select:
SELECT a.summary
FROM t_t_campaign_rule a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId}

SELECT a.number,a.voteCount,a.headPic,a.name,a.id
FROM t_t_user a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId}


            a.name like CONCAT('%',#{queryKey},'%') OR a.number like CONCAT('%',#{queryKey},'%')


ORDER BY a.number ASC

    LIMIT #{start},#{pageSize}


    LIMIT #{pageSize}

SELECT COUNT(1)
FROM t_t_user a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId} AND a.id = #{userId}

SELECT a.headImgUrl,a.nickName,a.voteTime,a.giftCount,a.giftName,a.remark,a.voteTimestamp
FROM t_t_user_gift_record a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId} AND a.userId = #{userId} AND a.voteTimestamp < #{voteTimestamp}
ORDER BY a.voteTime DESC

    LIMIT #{start},#{pageSize}


    LIMIT #{pageSize}

SELECT COUNT(1)
FROM t_t_user_gift_record a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId} AND a.userId = #{userId}

mapper.update:
UPDATE t_t_campaign_view a
LEFT JOIN t_t_user b ON a.chainId = b.chainId AND b.id = #{userId}
SET a.viewCount = a.viewCount + #{incrCampaignViewCount},b.viewCount = b.viewCount + #{incrUserViewCount}
WHERE a.chainId = #{chainId}

mapper.select:
SELECT a.nickname,a.headimgurl
FROM t_t_voter a
WHERE a.openid = #{openid}

SELECT a.id AS giftId,concat(a.giftName,if(isnull(a.giftDesc),'',concat('(',a.giftDesc,')'))) AS giftName,a.giftName AS giftBaseName,
      a.giftpic,a.giftPoint,a.voteCount,a.giftFee
FROM t_t_campaign_gift a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId}

        AND a.id = #{giftId}

ORDER BY a.giftPoint ASC

mapper.update:
UPDATE t_t_user_gift_record a
SET a.dataStatus = 1,a.openId = #{openId}
WHERE a.orderId = #{orderId} AND a.dataStatus = 0

UPDATE t_t_user a
LEFT JOIN t_t_campaign_vote b ON a.chainId = b.chainId
SET a.id = #{userId}

    ,a.giftPoint = a.giftPoint + #{giftPoint}


    ,a.voteCount = a.voteCount + #{voteCount},b.voteCount = b.voteCount + #{voteCount}

WHERE a.id = #{userId}

mapper.select:
SELECT a.headPic,a.name,a.number,a.voteCount,a.giftPoint,a.id AS userId,a.declaration
FROM t_t_user a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId}
ORDER BY a.voteCount DESC

    LIMIT #{start},#{pageSize}


    LIMIT #{pageSize}

SELECT count(*)
FROM t_t_user a
WHERE a.dataStatus = 1 AND a.chainId = #{chainId}
